<script>    import {onMount} from "svelte";    import * as THREE from 'three'    import {GLTFLoader} from "three/examples/jsm/loaders/GLTFLoader.js";    import { OrbitControls} from "three/examples/jsm/controls/OrbitControls.js"    let container    onMount(()=>{        const height = window.innerHeight        const width = window.innerWidth        let mixer        const renderer = new THREE.WebGLRenderer({ alpha:true ,antialias:true})        renderer.setPixelRatio(window.devicePixelRatio)        renderer.setSize(width, height)        renderer.outputEncoding = THREE.sRGBEncoding        container.appendChild(renderer.domElement)        window.addEventListener("resize",onWindowResize)        const scene = new THREE.Scene()        const camera = new  THREE.PerspectiveCamera(75, width/ height, 0.1,100)        camera.position.set( 0,0,1.5 );        const controls = new OrbitControls(camera, renderer.domElement)        controls.target.set(0,0,0)        controls.update()        controls.enablePan = false        controls.enableDamping= false        const loader = new GLTFLoader()        loader.load( "../animationBook/scene.gltf", function(gltf){            const model = gltf.scene            model.position.set( 0, 0, 0 );            model.scale.set(0.01,0.01,0.01)            scene.add(model)            mixer = new THREE.AnimationMixer(gltf.scene)            const action = mixer.clipAction(gltf.animations[0])            action.play()        } ,function(load){            console.log(load)        }, function(err){            console.error(err)        })        const light = new THREE.DirectionalLight(0xffffff, 1)        light.position.set(2,2,5)        scene.add(light)        const clock = new THREE.Clock();        function animate(){            requestAnimationFrame( animate );            const delta = clock.getDelta();            mixer.update( delta );            controls.update();            renderer.render( scene, camera );        }        function onWindowResize() {            camera.aspect = window.innerWidth / window.innerHeight;            camera.updateProjectionMatrix();            renderer.setSize( window.innerWidth, window.innerHeight );            render();        }        function render(){            renderer.render(scene, camera)        }        animate()    })</script><div bind:this={container}></div>