<script>    import {onMount} from "svelte";    import { OrbitControls} from "three/examples/jsm/controls/OrbitControls.js"    import * as THREE from 'three'    let container    onMount(()=>{        const scene = new THREE.Scene();        const camera = new THREE.PerspectiveCamera( 75, window.innerWidth / window.innerHeight, 0.1, 1000 );        const renderer = new THREE.WebGLRenderer({ antialias: true});        renderer.setSize( window.innerWidth, window.innerHeight );        document.body.appendChild( renderer.domElement );        window.addEventListener("resize",onWindowResize)        const floor = new THREE.Mesh(            new THREE.PlaneGeometry(20, 20),            new THREE.MeshStandardMaterial({ color: "#a9c388" })        );        floor.rotation.x = -Math.PI * 0.5;        floor.position.y = 0;        scene.add(floor);        function onWindowResize() {            camera.aspect = window.innerWidth / window.innerHeight;            camera.updateProjectionMatrix();            renderer.setSize( window.innerWidth, window.innerHeight );            render();        }        function render(){            renderer.render(scene, camera)        }        render()    })</script><div bind:this={container}></div>