<script>    import * as THREE from 'three';    import * as CANNON from 'cannon-es'    import { OrbitControls} from "three/examples/jsm/controls/OrbitControls.js"    import CannonDebugger from 'cannon-es-debugger';    const scene = new THREE.Scene();    const camera = new THREE.PerspectiveCamera( 75, window.innerWidth / window.innerHeight, 0.1, 1000 );    const renderer = new THREE.WebGLRenderer({ antialias: true});    renderer.setSize( window.innerWidth, window.innerHeight );    document.body.appendChild( renderer.domElement );    const physicsWorld = new CANNON.World({        gravity: new CANNON.Vec3(0, -9.82, 0),    });    const groundBody = new CANNON.Body({        type: CANNON.Body.STATIC,        // infinte geometric plane        shape: new CANNON.Plane(),    });    // rotate ground body by 90 degrees    groundBody.quaternion.setFromEuler(-Math.PI / 2, 0, 0);    physicsWorld.addBody(groundBody);    const radius = 1;    const sphereBody = new CANNON.Body({        mass: 5,        shape: new CANNON.Sphere(radius),    });    physicsWorld.addBody(sphereBody);    const controls = new OrbitControls(camera, renderer.domElement)    controls.target.set(0,0,0)    controls.update()    controls.enablePan = false    controls.enableDamping= false    // const geometry = new THREE.BoxGeometry( 1, 1, 1 );    // const material = new THREE.MeshBasicMaterial( { color: 0x00ff00 } );    // const cube = new THREE.Mesh( geometry, material );    // scene.add( cube );    const geometry = new THREE.SphereGeometry( 1, 32, 16 );    const material = new THREE.MeshBasicMaterial( { color: 0xffff00 } );    const sphere = new THREE.Mesh( geometry, material );    scene.add( sphere );    //    // const floor = new THREE.Mesh(    //     new THREE.PlaneGeometry(10, 10),    //     new THREE.MeshStandardMaterial({ color: "#a9c388" })    // );    // floor.rotation.x = -Math.PI * 0.5;    // floor.position.y = 0;    // scene.add(floor);    camera.position.z = 5;    camera.position.y = 2;    const cannonDebugger = new CannonDebugger(scene, physicsWorld, {        // color: 0xff0000,    });    function animate() {        requestAnimationFrame( animate );        // cube.rotation.x += 0.01;        // cube.rotation.y += 0.01;        physicsWorld.fixedStep();        sphere.quaternion.copy(sphereBody.quaternion)        sphere.position.copy(sphereBody.position)        renderer.render( scene, camera );        cannonDebugger.update();    };    function onWindowResize() {        camera.aspect = window.innerWidth / window.innerHeight;        camera.updateProjectionMatrix();        renderer.setSize( window.innerWidth, window.innerHeight );        render();    }    function render(){        renderer.render(scene, camera)    }    // render()    animate();</script><div></div>