<script>    import * as THREE from 'three';    import { OrbitControls} from "three/examples/jsm/controls/OrbitControls.js"    const scene = new THREE.Scene();    const camera = new THREE.PerspectiveCamera( 75, window.innerWidth / window.innerHeight, 0.1, 1000 );    const renderer = new THREE.WebGLRenderer({alpha: true , antialias: true});    renderer.setSize( window.innerWidth, window.innerHeight );    document.body.appendChild( renderer.domElement );    const controls = new OrbitControls(camera, renderer.domElement)    controls.target.set(0,0,0)    controls.update()    controls.enablePan = false    controls.enableDamping= false    const geometry = new THREE.BoxGeometry( 1, 1, 1 );    const material = new THREE.MeshBasicMaterial( { color: 0x00ff00 } );    const cube = new THREE.Mesh( geometry, material );    scene.add( cube );    const floor = new THREE.Mesh(        new THREE.PlaneGeometry(20, 20),        new THREE.MeshStandardMaterial({ color: "#a9c388" })    );    floor.rotation.x = -Math.PI * 0.5;    floor.position.y = 0;    scene.add(floor);    camera.position.z = 5;    function animate() {        requestAnimationFrame( animate );        cube.rotation.x += 0.01;        cube.rotation.y += 0.01;        renderer.render( scene, camera );    };    function onWindowResize() {        camera.aspect = window.innerWidth / window.innerHeight;        camera.updateProjectionMatrix();        renderer.setSize( window.innerWidth, window.innerHeight );        render();    }    function render(){        renderer.render(scene, camera)    }    animate();</script><div></div>