<script>    import * as THREE from 'three';    import { onMount } from 'svelte';    let container    onMount(() => {        const scene = new THREE.Scene()        const camera = new THREE.PerspectiveCamera(75, window.innerWidth / window.innerHeight, 0.1, 500)        camera.position.set( 0, 0, 50 );        const renderer = new THREE.WebGLRenderer({ antialias:true})        renderer.setSize( window.innerWidth, window.innerHeight );        container.appendChild( renderer.domElement );        const material = new THREE.LineBasicMaterial({color:"red"})        const points = []        points.push( new THREE.Vector3( - 15, 0, 0 ) );        points.push( new THREE.Vector3( 0, 15, 0 ) );        points.push( new THREE.Vector3( 15, 0, 0 ) );        const geometry = new THREE.BufferGeometry().setFromPoints(points)        const line = new THREE.Line(geometry,material)        scene.add(line)        renderer.render(scene,camera)    });</script><div bind:this={container} style={{width: "100vw", height: "100vh"}}></div>