<script> import * as THREE from 'three' import {GLTFLoader} from "three/examples/jsm/loaders/GLTFLoader.js" import {DRACOLoader} from "three/examples/jsm/loaders/DRACOLoader.js" import {onMount} from "svelte"; let container onMount(()=>{     const renderer = new THREE.WebGLRenderer({ antialias : true})     renderer.setPixelRatio( window.devicePixelRatio );     renderer.setSize( window.innerWidth, window.innerHeight );     renderer.outputEncoding = THREE.sRGBEncoding;     container.appendChild(renderer.domElement)     window.addEventListener( 'resize', onWindowResize );     const scene = new THREE.Scene();     const camera = new THREE.PerspectiveCamera( 75, window.innerWidth / window.innerHeight, 0.1, 100 );     camera.position.set( 0,1,2.5 );     // const dracoLoader = new DRACOLoader()     // dracoLoader.setDecoderPath("../gltf/")     const loader = new GLTFLoader()     // loader.setDRACOLoader(dracoLoader)     loader.load( '../gltf/scene.gltf', function ( gltf ) {         const model = gltf.scene;         model.position.set( 0, 0.3, 0 );         model.scale.set(0.07,0.07,0.07)         scene.add( model );     }, undefined, function ( e ) {         console.error( e );     } );    const light = new THREE.DirectionalLight(0xffffff, 1)     light.position.set(2,2,5)     scene.add(light)     renderer.render(scene, camera)     function animate(){         requestAnimationFrame( animate );         render()     }     function onWindowResize() {         camera.aspect = window.innerWidth / window.innerHeight;         camera.updateProjectionMatrix();         renderer.setSize( window.innerWidth, window.innerHeight );         render();     }     function render(){         renderer.render(scene, camera)     }     animate() })</script><div bind:this={container}></div>